import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import HomePage from './image/logo/login.png';
import './css/App.css';

function Home() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    // Check if the username and password are not empty
    if (!username || !password) {
      setError('Please enter both username and password.');
      return;
    }

    // Perform login logic here
    // For simplicity, let's check if the username is '001' or '002'
    if (username === '001') {
      // Navigate to the home page for user '001'
      navigate('/home');
    } else if (username === '002') {
      // Navigate to the admin home page for user '002'
      navigate('/admin-home');
    } else {
      // Handle the case where the username is not recognized
      setError('Invalid username');
    }
  };

  return (
    <div className="bg-container">
      <img src={HomePage} alt="UGBN" className="background-image" />

      <div className="login-form">
        <input
          type="text"
          id="username"
          className="transparent-placeholder"
          placeholder="Username:"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ fontSize: '20px' }} // Increase text size as needed
        />

        <input
          type="password"
          id="password"
          className="transparent-placeholder"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ fontSize: '20px' }} // Increase text size as needed
        />

        {error && <p className="error-message">{error}</p>}

        <button onClick={handleLogin} style={{ fontSize: '20px' }}>Login</button>
      </div>
    </div>
  );
}

export default Home;
